import React, { Component } from 'react';
import Styled from "../style/styled";


export default class ConfirmDelete extends Component {
    constructor(props){
        super(props)
        this.state={
            show:true
        }
        this.handleClick=this.handleClick.bind(this);
    }

    handleClick(){
        if(this.state.confirm){
            this.props.delete(this.props.theItem)
            this.setState({color:"white", colorBackground:"#2B4141", confirm:false})
        }
        this.setState({color:"black", colorBackground:"red", confirm:true})

    }
    render() {
        let deleteButton;
        if(this.state.show){
            deleteButton = <Styled.ConfirmDelete onClick={this.setState({show:false})}>Delete</Styled.ConfirmDelete>
        } 

        return (
            <>
            {deleteButton}
            </>
        )
    }
}
