import React, { Component } from 'react';
import Styled from "../style/styled";
import {connect} from "react-redux";

class ConfirmDelete extends Component {
    constructor(props){
        super(props)
        this.state={
            color:"white",
            colorBackground:"#2B4141",
            confirm:false
        }
        this.handleClick=this.handleClick.bind(this);
    }

    handleClick(){
        if(this.state.confirm){
            console.log(this.props.isLoading)
            this.props.delete(this.props.theItem)
            console.log(this.props.isLoading)
        }
        this.setState({color:"black", colorBackground:"red", confirm:true})

    }
    render() {
        return (

            <Styled.ConfirmDelete disabled={this.props.isLoading} color={this.state.color} colorBackground={this.state.colorBackground} onClick={()=>this.handleClick()}>
                Delete
            </Styled.ConfirmDelete> 
        )
    }
}

const mapStateToProps= (state)=>{
    const {isLoading} = state;
    return {isLoading}
}

export default connect(mapStateToProps)(ConfirmDelete)
