import React, { Component } from 'react';
import Styled from "../style/styled";


export default class ConfirmDelete extends Component {
    constructor(props){
        super(props)
        this.state={
            show:false
        }
        this.handleClick=this.handleClick.bind(this);
    }

    handleClick(){
        if(this.state.confirm){
            this.props.delete(this.props.theItem)
            this.setState({color:"white", colorBackground:"#2B4141", confirm:false})
        }
        this.setState({color:"black", colorBackground:"red", confirm:true})

    }
    render() {

        if(this.state.show){
            deleteButton = <Styled.ConfirmDelete>Delete</Styled.ConfirmDelete>
        } 

        return (

            <>
            <Styled.ConfirmDelete visibility={!this.state.visibility}>
                Delete
            </Styled.ConfirmDelete>

            <Styled.ConfirmDelete visbility={this.state.visibility}color={this.state.color} colorBackground={this.state.colorBackground} onClick={()=>this.handleClick()}>
                Confirm Delete
            </Styled.ConfirmDelete>
            </> 
        )
    }
}
